const roll = await new Roll("1d6").roll();

const message = await roll.toMessage();
await game.dice3d?.waitFor3DAnimationByMessageID(message.id);
if (roll.total > 1)
  return;

return new invincible.utils.InvincibleRollDialog(
  "Experiment Supplies - Explosion",
  {
    breakdown: {
      "Blast Rating": 4,
    },
    attackInfo: {
      damage: 2,
      minRange: 0,
      maxRange: 1
    },
    maxPush: 0
  }
).wait({shiftKey: true});
