const values = {
  true: {
    "system.attributes.fighting.value": 3,
    "system.attributes.agility.value": 3,
    "system.attributes.strength.value": 2
  },
  false: {
    "system.attributes.fighting.value": 7,
    "system.attributes.agility.value": 5,
    "system.attributes.strength.value": 8
  }
};

const isTransformed = actor.system.attributes.strength.value == 8;
const valuesToUpdate = values[isTransformed];

if (isTransformed) {
  valuesToUpdate["system.derived.resolve.value"] = actor.system.derived.resolve.value - 1;
  valuesToUpdate["system.derived.health.value"] = actor.system.derived.health.value - 6;
}
else {
  valuesToUpdate["system.derived.health.value"] = actor.system.derived.health.value + 6;
}

await actor.update(valuesToUpdate);
