command:
const modifier = await foundry.applications.api.DialogV2.input({
  window: { title: "Critical Injury" },
  content: `<p><label>Modifier: </label><input type="number" name="modifier" value="0" style="width: 100%;" /></p>`,
  ok: {
    label: "Roll",
    icon: "fa-solid fa-dice",
  }
});

if (!modifier)
  return;

const roll = await new Roll(`1d6 + ${modifier.modifier}`).roll();
await (await fromUuid("RollTable.lRIHdMV6UBdDaeae")).draw({ rollMode: CONST.DICE_ROLL_MODES.PRIVATE, roll });
